import Link from "next/link";

export default class extends React.Component {

static async getInitialProps({query}){
    let idChannel = query.id;

    let [reqChannel, reqSeries, reqAudio] = await Promise.all([
        fetch(`https://api.audioboom.com/channels/${idChannel}`),
        fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`),
        fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`)
    ])


    //let reqChannel = await fetch(`https://api.audioboom.com/channels/${idChannel}`);
    let dataChannel =  await reqChannel.json();
    let canal = dataChannel.body;
    
    

    //let reqAudio = await fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`);
    let dataAudios = await reqAudio.json();
    let audioClips = dataAudios.body.audio_clips;

    //let reqSeries = await fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`);
    let dataSeries = await reqSeries.json();
    let series = dataSeries.body.channels;

    return {canal, audioClips, series}
}

    render(){
        const { canal, audioClips, series}= this.props;
        return <>
                <header>Podcast</header>
                <h1>{canal.channel.title}</h1>
                <div className="chann">
                <img src={canal.channel.urls.logo_image.original} alt=""/>
                </div>


                <h2>Series</h2>
                <div className="channels">
                { series.map ((serie) =>(
                    <Link href={`/podcast?id=${ serie.id}`} prefetch>
                        <a className="channel">
                            <img src={serie.urls.logo_image.original} alt=""/>
                        <h2>{serie.title}</h2>
                        </a>
                    </Link>
                ))}
                </div>


                <h2>Ultimos Podcasts</h2>
                <div className="channels">
                { audioClips.map ((clip) =>(
                    <Link href={`/podcast?id=${clip.id}`} prefetch>
                    <a className="channel">
                        <img src={clip.channel.urls.logo_image.original} alt="" />
                    <h2>{clip.title}</h2>
                    </a>
                    </Link>
                ))}
                </div>


                <style jsx>{`
                            header {
                                color:#fff;
                                background: #8756ca;
                                padding: 15px;
                                text-align: center;
                            }
                            .channels {
                                display: grid;
                                grid-gap: 15px;
                                padding: 15px;
                                grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
                            }
    
                            .channel {
                                display: block;
                                border-radius: 3px;
                                box-shadow: 0px 2px 6px rgba(0,0,0,0.15);
                                margin-bottom: 0.5em;
                              }
                              .channel img {
                                  width: 100%;
                              }
                              .chann img{
                                  width: 50%;
                                  height: 250px;
                                  padding: 20px;
                              }
                              h1{
                                  padding: 15px;
                              }
                              h2 {
                                padding: 5px;
                                font-size: 0.9em;
                                font-weight: 600;
                                margin: 0;
                                text-align: center;
                              }
                        `}</style>
                        
                        
                        <style jsx global>{`
                        body{
                            margin: 0;
                            font-family: system-ui;
                            background: white;
                        }        
                    `}</style>
               </>

    }
}
