                <style jsx>{`
                h1 {
                    color:red;
                }
                :global(p) {
                    color:green;
                }                              
                `}</style>
                                <style jsx global>{`
                    body{
                        background: white;
                    }        
                `}</style>
             
              </>
    }
}
